package mypro01;

public class TestSwitch02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a = "郭帅";
			switch(a){
			case "马士兵":
				System.out.println("输入的马士兵");
				break;
			case "郭帅":
				System.out.println("输入郭帅");
				break;
			default:
				System.out.println("输入其他");
			}
	}

}
